import { ref, onMounted } from 'vue';
import axios from 'axios';
export default {
  __name: 'OrdersView',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const orders = ref({
      status: 'loading',
      orders: []
    });
    const fetchOrders = async () => {
      try {
        const res = await axios.get('http://localhost:8080/api/orders', {
          withCredentials: true
        }).then(res => {
          if (res.data.status === 'success') {
            orders.value = {
              status: 'success',
              orders: [res.data.order]
            };
          } else {
            orders.value = {
              status: 'error',
              orders: []
            };
          }
        });
        console.log('res.data:', res.data); // <- Bu satırı ekle
        orders.value = res.data;
      } catch (err) {
        console.error(err);
        orders.value = {
          status: 'error',
          orders: []
        };
      }
    };
    onMounted(() => {
      fetchOrders();
    });
    const __returned__ = {
      orders,
      fetchOrders,
      ref,
      onMounted,
      get axios() {
        return axios;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
